$offset = (int) $offset ;
if ($offset <= 0) $offset = 0;

if (!isset($repeat)) $repeat = 1;
//$repeat = (int)$repeat ;
//if ($repeat <= 0) $repeat = 1;

if (!isset($blocks)) $blocks = 1;
$blocks = (int)$blocks ;
if ($blocks <= 0) $blocks = 1;

if ($blocks > count($text[$set])) $blocks = count($text[$set]);
$returnvalue = "";
if (isset($debug)) $returnvalue .= "<small>set:".$set." offset:".$offset." repeat:".$repeat." blocks:".$blocks." end debug</small>";

for($i=1;$i<=$repeat;$i++) {
	$returnvalue .= implode("<br /><br />", array_slice($text[$set], $offset, $blocks))."<br /><br />";
}
return $returnvalue;
